// Fun��o para ajustar valores dos campos num�ricos
function adjustValue(fieldName, increment) {
    const field = document.querySelector(`input[name="${fieldName}"]`);
    if (field) {
        const currentValue = parseFloat(field.value) || 0;
        const newValue = Math.max(0, currentValue + increment);
        
        // Determinar n�mero de casas decimais baseado no campo
        let decimalPlaces = 0;
        if (fieldName === 'fat_percentage' || fieldName === 'protein_percentage') {
            decimalPlaces = 2;
        }
        
        field.value = newValue.toFixed(decimalPlaces);
        
        // Adicionar feedback visual
        const color = increment > 0 ? '#fef3c7' : '#fef2f2';
        field.style.backgroundColor = color;
        setTimeout(() => {
            field.style.backgroundColor = '';
        }, 300);
        
        // Disparar evento de input para valida��o
        field.dispatchEvent(new Event('input'));
    }
}

// Fun��o para validar valores em tempo real
function validateQualityInputs() {
    const inputs = document.querySelectorAll('#qualityForm input[type="number"]');
    inputs.forEach(input => {
        input.addEventListener('input', function() {
            const value = parseFloat(this.value);
            const fieldName = this.name;
            
            // Valida��es espec�ficas por campo
            switch(fieldName) {
                case 'fat_percentage':
                    if (value < 0 || value > 100) {
                        this.style.borderColor = '#ef4444';
                        this.style.backgroundColor = '#fef2f2';
                    } else if (value < 3.0 || value > 6.0) {
                        this.style.borderColor = '#f59e0b';
                        this.style.backgroundColor = '#fffbeb';
                    } else {
                        this.style.borderColor = '#10b981';
                        this.style.backgroundColor = '#f0fdf4';
                    }
                    break;
                    
                case 'protein_percentage':
                    if (value < 0 || value > 100) {
                        this.style.borderColor = '#ef4444';
                        this.style.backgroundColor = '#fef2f2';
                    } else if (value < 2.8 || value > 4.0) {
                        this.style.borderColor = '#f59e0b';
                        this.style.backgroundColor = '#fffbeb';
                    } else {
                        this.style.borderColor = '#10b981';
                        this.style.backgroundColor = '#f0fdf4';
                    }
                    break;
                    
                case 'scc':
                    if (value < 0) {
                        this.style.borderColor = '#ef4444';
                        this.style.backgroundColor = '#fef2f2';
                    } else if (value > 400) {
                        this.style.borderColor = '#ef4444';
                        this.style.backgroundColor = '#fef2f2';
                    } else if (value > 300) {
                        this.style.borderColor = '#f59e0b';
                        this.style.backgroundColor = '#fffbeb';
                    } else {
                        this.style.borderColor = '#10b981';
                        this.style.backgroundColor = '#f0fdf4';
                    }
                    break;
                    
                case 'total_bacterial_count':
                    if (value < 0) {
                        this.style.borderColor = '#ef4444';
                        this.style.backgroundColor = '#fef2f2';
                    } else if (value > 100) {
                        this.style.borderColor = '#ef4444';
                        this.style.backgroundColor = '#fef2f2';
                    } else if (value > 50) {
                        this.style.borderColor = '#f59e0b';
                        this.style.backgroundColor = '#fffbeb';
                    } else {
                        this.style.borderColor = '#10b981';
                        this.style.backgroundColor = '#f0fdf4';
                    }
                    break;
            }
            
            // Reset ap�s 2 segundos
            setTimeout(() => {
                this.style.borderColor = '';
                this.style.backgroundColor = '';
            }, 2000);
        });
    });
}

