// Upload profile photo to Database Storage
async function uploadProfilePhoto(file, userId) {
    try {
        // Usando MySQL direto atrav�s do objeto 'db'
        
        // Get current authenticated user
        const { data: { user }, error: authError } = await db.auth.getUser();
        
        if (authError || !user) {
            console.error('DEBUG: Falha na autentica��o:', authError);
            throw new Error('Usu�rio n�o autenticado');
        }
        
        // Usu�rio da fazenda Lagoa Do Mato for organizing photos by farm

        const { data: managerData, error: managerError } = await db
            .from('users')
            .select('id')
            .eq('id', user.id)
            .single();
        

        
        if (managerError || !managerData) {
            console.error('DEBUG: Fazenda n�o encontrada:', managerError);
            throw new Error('Usu�rio n�o encontrado');
        }
        
        const fileExt = file.name.split('.').pop();
        // Ensure we always use the target userId, never fallback to current user
        if (!userId) {
            throw new Error('userId � obrigat�rio para upload de foto');
        }
        
        // Create unique filename with more specific naming
        const timestamp = Date.now();
        const randomId = Math.random().toString(36).substr(2, 9);
        const fileName = `user_${userId}_${timestamp}_${randomId}.${fileExt}`;
        const filePath = `lagoa-do-mato/${fileName}`; // Fazenda �nica
        
        const { data, error } = await db.storage
            .from('profile-photos')
            .upload(filePath, file, {
                cacheControl: '3600',
                upsert: false
            });
        

        
        if (error) {
            console.error('DEBUG: Erro no upload:', {
                message: error.message,
                statusCode: error.statusCode,
                error: error
            });
            throw error;
        }
        

        
        const { data: { publicUrl } } = db.storage
            .from('profile-photos')
            .getPublicUrl(filePath);
        

        return publicUrl;
    } catch (error) {
        console.error('DEBUG: Erro final:', {
            message: error.message,
            stack: error.stack,
            error: error
        });
        throw error;
    }
}

