        // Get all users from the same farm (without photos to avoid cache issues)
        const { data: allUsers, error } = await db
            .from('users')
            .select('id, name, email, role, whatsapp, is_active, created_at')
            .eq('farm_id', 1)
            .order('created_at', { ascending: false });
        
        if (error) {
