        // Fun��es diretas para aprovar e rejeitar (sem depender de currentPasswordRequestId)
        window.approvePasswordRequestDirect = async function(requestId) {
            if (!requestId) {
                showNotification('ID da solicita��o n�o encontrado', 'error');
