                console.log('? Solicita��o aprovada com sucesso!');
                
                // Log de auditoria
                logAuditEvent('password_request_approved', request.user_id, {
                    requestId: requestId,
                    managerId: user.id,
                    newPasswordLength: newPassword.length,
                    reason: request.reason
